import React from 'react'
import styled from "styled-components"
import BlankPhone from "../images/sideways-phone-blank.svg"

const Container = styled.div`
    width: 100%;
    height: 740px;
    background-color: #fff;
    font-family: 'Lato', sans-serif;
    color: #111439;
    padding-top: 20px;
    margin-top: 60px;
`

const ContentContainer = styled.div`
    width: 1100px;
    height: 600px;
    margin-left: 50%;
    transform: translateX(-50%);
    display: flex;
`

const TextContainer = styled.div`
    margin-left: 50px;
    // background-color: #f00;
    width: 40%;
`

const ProjectIdDiv = styled.div`
    position: absolute;
    left: -1px;
    width: 2px;
    height: 14px;
    background-color: #6318FF;
`

const ProjectId = styled.h2`
    font-size: 14px;
    color: #CDCDE3;
    margin-bottom: 20px;
    background: -webkit-linear-gradient(#6E29FF, #C479FF);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;font-weight: 700;

`

const ProjectHeader = styled.h1`
    font-size: 32px;
    font-weight: 800;
`

const ProjectText = styled.p`
    margin-top: 20px;
    font-weight: 300;
    line-height: 24px;
    margin-bottom: 20px;
`

const PhoneContainer = styled.div`
    width: 50%;
    margin-top: -70px;
    // background-color: #0f0;
`

const PhoneImg= styled.img`
    margin-left: 30px;
`

const ViewMoreButton = styled.a`
    font-weight: 500;
    line-height: 24px;
    color: #CDCDE3;
`

const GitHubIconHolder = styled.div`
    font-size: 1.6rem;
    color: #DADDED;
    position: absolute;
    top: 10px;
    right: -6px;
    transition: 0.3s;
    &:hover {
        color: #A5ACD9;
        text-shadow:3px 6px rgba(255,165,0,.75)
    }
`

const LinkIconHolder = styled.div`
    font-size: 1.3rem;
    color: #DADDED;
    position: absolute;
    top: 46px;
    right: -6px;
    transition: 0.3s;
    &:hover {
        color: #A5ACD9;
        text-shadow:3px 6px rgba(255,165,0,.75)
    }
`


export default function ProjectSection () {
    return (
        <Container>
            <ContentContainer>
            <TextContainer>
            <ProjectIdDiv></ProjectIdDiv>
            <ProjectId>Personal Project</ProjectId>
            <ProjectHeader>Twitch User Profile</ProjectHeader>
            <ProjectText>
                A web app for visualizing personalized Spotify data. 
                View your top artists, top tracks, recently played tracks,
                 and detailed audio information about each track. Create 
                 and save new playlists of recommended tracks based on your 
                 existing playlists and more.
            </ProjectText>
            <GitHubIconHolder><i class="fab fa-github-alt"></i></GitHubIconHolder>
            <LinkIconHolder><i class="fas fa-external-link-alt"></i></LinkIconHolder>
            <ViewMoreButton>View More Details </ViewMoreButton>
            </TextContainer>
            <PhoneContainer>
                    <PhoneImg src={BlankPhone}/>
                </PhoneContainer>
            </ContentContainer>
        </Container>
    )
}
