import React from 'react'
import styled from 'styled-components'
import bg from '../../images/books-bg.svg'
import themes from "../../themes"

const Container = styled.div`
    position: absolute;
    width: 28%;
    top: 70px;
    right: 100px;
    height: 360px;
    z-index: 1;
    @media (max-width: 1180px) {
        width: 28%;
        top: 62px;
        right: 40px;
        height: 290px;
    }
    @media (max-width: 960px) {
        width: 28%;
        top: 60px;
        right: 90px;
        height: 294px;
    }
    @media (max-width: 800px) {
        position: absolute;
        right: 16%;
        width: 68%;
        height: auto;
        margin: 0;
        margin-top: 28px;
    }
    @media (max-width: 516px) {
        margin-top: 20px;
    }
`

const ViewMoreButton = styled.a`
    font-weight: 500;
    line-height: 18px;
    font-size: 11px;
    color: ${props => props.theme === "light" ? themes.light.viewMoreBtnColor : themes.dark.viewMoreBtnColor};
    text-decoration: none;
    transition: 0.2s;
`

const Arrow1 = styled.div`
    width: 2px;
    height: 6px;
    background-color: ${props => props.theme === "light" ? themes.light.viewMoreBtnColor : themes.dark.viewMoreBtnColor};
    transform:rotate(90deg);
    position: absolute;
    transform: rotate(45deg);
    top: 11px;
    left: 105px;
`

const Arrow2 = styled.div`
    width: 2px;
    height: 5px;
    background-color: ${props => props.theme === "light" ? themes.light.viewMoreBtnColor : themes.dark.viewMoreBtnColor};
    position: absolute;
    transform: rotate(-45deg);
    top: 7px;
    left: 105px;
`

const Arrow3 = styled.div`
    width: 6px;
    height: 2px;
    position: absolute;
    top: 12px;
    left: 105px;
    background-color: ${props => props.theme === "light" ? themes.light.viewMoreBtnHoverColor: themes.dark.viewMoreBtnHoverColor};
    opacity: 0;
    // border-radius: 1px;
`

const ButtonHolder = styled.div`
    transition: 0.1s;
    top: 83px;
    right: 176px;
    position: absolute;
    // background-color: #f00;
    // margin-top: 60px;
    &:hover {
        ${ViewMoreButton} {
            color: ${props => props.theme === "light" ? themes.light.viewMoreBtnHoverColor: themes.dark.viewMoreBtnHoverColor};
          }
          ${Arrow1} {
            transition: 0.1s ease-in-out;
            background-color: ${props => props.theme === "light" ? themes.light.viewMoreBtnHoverColor: themes.dark.viewMoreBtnHoverColor};
            left: 112px;
          }
          ${Arrow2} {
            transition: 0.1s ease-in-out;
            background-color: ${props => props.theme === "light" ? themes.light.viewMoreBtnHoverColor: themes.dark.viewMoreBtnHoverColor};
            left: 112px;
          }
        ${Arrow3} {
            transition: 0.1s ease-in;
            opacity: 1;
        }
    }
`

const BgImg = styled.img`
    margin: 0;
    padding: 0;
    width: 100%;
`

const ReturnDiv = styled.div`

`

const Books = ({ theme, setTheme }) => {
  return (
    <Container>
        <BgImg src={bg}></BgImg>
        <ButtonHolder className="fadeInProject" theme={theme}>
                <ViewMoreButton theme={theme} target="_blank" href={"google.com"} >View More Details </ViewMoreButton>
                <Arrow1 theme={theme}/>
                <Arrow2 theme={theme}/>
                <Arrow3 theme={theme}/>
        </ButtonHolder>
    </Container>
  )
}

export default Books
