import React from 'react'
import styled from "styled-components"
import BlankPhone from "../images/blank-phone-vertical.svg"
import Zelda from "../images/AboutPhone/zelda.png"
import Tyler from "../images/AboutPhone/tyler.png"
import Kevin from "../images/AboutPhone/kevin.png"
import Photos from "../images/AboutPhone/photos.svg"
import Twitch from "../images/AboutPhone/twitch.svg"
import Spotify from "../images/AboutPhone/spotify.svg"
import Swift from "../images/AboutPhone/swift.svg"
import ReactImg from "../images/AboutPhone/react.svg"
import JS from "../images/AboutPhone/js.svg"

const Container = styled.div`
    width: 100%;
    height: 620px;
    background-color: #fff;
    font-family: 'Lato', sans-serif;
    color: #111439;
    padding-top: 20px;
`

const ContentContainer = styled.div`
    width: 1100px;
    height: 600px;
    padding-top: 30px;
    margin-left: 50%;
    transform: translateX(-50%);
    display: flex;
`

const TextContainer = styled.div`
    margin-top: 60px;
    margin-left: 50px;
    // background-color: #f00;
    width: 50%;
`

const AboutIdDiv = styled.div`
    position: absolute;
    left: -1px;
    width: 2px;
    height: 14px;
    background-color: #CDCDE3;
`

const AboutId = styled.h2`
    font-size: 14px;
    color: #CDCDE3;
    margin-bottom: 20px;
    font-weight: 700;
`

const AboutHeader = styled.h1`
    font-size: 32px;
    font-weight: 800;
`

const AboutText = styled.p`
    margin-top: 20px;
    font-weight: 300;
    line-height: 24px;
`

const PhoneContainer = styled.div`
    width: 50%;
    margin-top: -40px;
    // background-color: #0f0;
    z-index: 7;
`

const PhoneContentContainer = styled.div`
    z-index: 9;
`

const ZeldaImg = styled.img`
    position: absolute;
    top: 274px;
    right: 97px;
    width: 170px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.05);
        opacity: 0.8;
    }
`

const TylerImg = styled.img`
    position: absolute;
    top: 320px;
    right: 266px;
    width: 140px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.04);
        opacity: 0.8;
    }
`

const KevinImg = styled.img`
    position: absolute;
    top: 60px;
    right: 97px;
    width: 310px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.04);
        opacity: 0.8;
    }
`

const PhotosImg = styled.img`
    position: absolute;
    top: 190px;
    right: 266px;
    width: 142px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.06);
        opacity: 0.8;
    }
`

const TwitchImg = styled.img`
    position: absolute;
    top: 190px;
    right: 180px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

const SpotifyImg = styled.img`
    position: absolute;
    top: 190px;
    right: 100px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

const SwiftImg = styled.img`
    position: absolute;
    top: 444px;
    right: 95px;
    width: 66px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const ReactAppImg = styled.img`
    position: absolute;
    top: 448px;
    right: 152px;
    width: 58px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const JSImg = styled.img`
    position: absolute;
    top: 444px;
    right: 202px;
    width: 66px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const PhoneImg= styled.img`
    margin-left: 50px;
`


export default function AboutSection () {
    return (
        <Container>
            <ContentContainer>
            <TextContainer>
            <AboutIdDiv></AboutIdDiv>
            <AboutId>Who am I ?</AboutId>
            <AboutHeader>A Little About Me ✌️</AboutHeader>
            <AboutText>
                Hi! My name is Ben and I enjoy creating things that live on the internet. 
                My interest in web development started back in 2012 when I decided to try editing 
                custom Tumblr themes — turns out hacking together a custom reblog button taught me 
                a lot about HTML & CSS!
            </AboutText>
            <AboutText>
                Fast-forward to today, and I've had the privilege of working at an advertising agency, 
                a start-up, a huge corporation, and a student-led design studio. My main focus these 
                days is building accessible, inclusive products and digital experiences at Upstatement 
                for a variety of clients. My main focus these 
                days is building accessible, inclusive products and digital experiences at Upstatement 
                for a variety of clients.
                </AboutText>
            </TextContainer>
            <PhoneContainer>
                    <PhoneImg src={BlankPhone}/>
                    <PhoneContentContainer>
                        <a href="https://www.youtube.com/watch?v=SMkvcKhVCEk" target="_blank">
                            <ZeldaImg src={Zelda}></ZeldaImg>
                        </a>
                        <a href="https://open.spotify.com/artist/4V8LLVI7PbaPR0K2TGSxFF" target="_blank">
                            <TylerImg src={Tyler}></TylerImg>
                        </a>
                        <a href="https://open.spotify.com/artist/5INjqkS1o8h1imAzPqGZBb" target="_blank">
                            <KevinImg src={Kevin}></KevinImg>
                        </a>
                        <a href="#" target="_blank">
                            <PhotosImg src={Photos}></PhotosImg>
                        </a>
                        <a href="https://www.twitch.tv/xqcow" target="_blank">
                            <TwitchImg src={Twitch}></TwitchImg>
                        </a>
                        <a href="https://open.spotify.com/user/b_dimarco" target="_blank">
                            <SpotifyImg src={Spotify}></SpotifyImg>
                        </a>
                        <a href="https://developer.apple.com/swift/" target="_blank">
                            <SwiftImg src={Swift}></SwiftImg>
                        </a>
                        <a href="https://reactjs.org/" target="_blank">
                            <ReactAppImg src={ReactImg}></ReactAppImg>
                        </a>
                        <a href="https://www.javascript.com/" target="_blank">
                            <JSImg src={JS}></JSImg>
                        </a>
                    </PhoneContentContainer>
                </PhoneContainer>
            </ContentContainer>
        </Container>
    )
}

// ✌️
