import React, { useState, useEffect } from "react"
import { ThemeProvider } from "styled-components"
import LandingSection from"./components/LandingSection"
import AboutSection from "./components/AboutSection"
import ProjectSection from "./components/ProjectSection"
import FooterSection from "./components/FooterSection"
import styled from "styled-components"
import BarLoader from "react-spinners/BarLoader";

const LightTheme = {
  secondaryBackgroundColor: "#F6F9FC",
  mainTextColor: "#111439",
  quoteTextColor: "#11143966",
  dottedLineColor: "#EBEBEB",

  primaryBackgroundColor: "#FFFFFF",
  aboutIdColor: "#CDCDE3",
  pTextColor: "#494B64",

  viewMoreBtnColor: "#CDCDE3",
  projectIconColor: "#DADDED",
  projectIconHoverColor: "#A5ACD9",

  copyrightTextColor: "#494B646",
  madeWithTextColor: "#494B64ee",
}

const DarkTheme = {
  secondaryBackgroundColor: "#15161A",
  mainTextColor: "#111439",
  quoteTextColor: "#11143966",
  dottedLineColor: "#EBEBEB",

  primaryBackgroundColor: "#FFFFFF",
  aboutIdColor: "#CDCDE3",
  pTextColor: "#494B64",

  viewMoreBtnColor: "#CDCDE3",
  projectIconColor: "#DADDED",
  projectIconHoverColor: "#A5ACD9",

  copyrightTextColor: "#494B646",
  madeWithTextColor: "#494B64ee",
}

// const Content = styled.div`
//   font-size: 4rem;
//   position: absolute;
//   left: 50%;
//   top: 50%;
//   transform: translate(-50%, -50%);
//   // background: #F00;
//   width: 70px;
// `

const LoaderDiv = styled.div`
  display: flex;
  justify-content: center; 
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: #F6F9FC;
  transition: 0.2s ease-in-out;
`

function App() {

  const [loading, setLoading] = useState(false) //true

  // useEffect(() => {
  //   setLoading(false)
  //   // setTimeout(() => {
  //   //   setLoading(false)
  //   // }, 3000)
  // }, [])

  return (
    <div>
      { loading ? 
      <LoaderDiv>
          <BarLoader 
            color={"#FF7474"} 
            loading={loading} 
            size={30} /> 
      </LoaderDiv>
      : 
        <div className="App">
          <LandingSection />
          <AboutSection />
          <ProjectSection 
            Color1="#6E29FF" 
            Color2="#C479FF" 
            ProjectType="Personal Project" 
            ProjectTitle="Twitch User Profile"  
            ProjectDesc="
            A web app for visualizing personalized Spotify data. 
            View your top artists, top tracks, recently played tracks, and 
            detailed audio information about each track. Create and save new 
            playlists of recommended tracks based on your existing playlists 
            and more." 
            ProjectDisplay={<></>} 
            ProjectGit=""
            ProjectLink=""
            />
          <ProjectSection 
            Color1="#396BFF" 
            Color2="#34BDFF" 
            ProjectType="Volunteer Organization" 
            ProjectTitle="WebPoint"  
            // ProjectDesc="Webpoint is a volunteer organization that delivers local 
            // businesses professional websites and provides students practical experience 
            // in web development. Our team is comprised of over 14 voluenteers, all 
            // of which are high school students." 
            ProjectDesc="Webpoint is a volunteer organization that delivers local 
            businesses professional websites and provides students practical experience 
            in web development." 
            ProjectDisplay={<></>} 
            ProjectGit="https://github.com/webpointco"
            ProjectLink="https://webpoint.dev/index.html"
            />
          {/* <ProjectSection /> */}
          <FooterSection />
        </div>
      } 
    </div>
  );
}

export default App;
