import React from 'react'
import styled from "styled-components"
import NavBar from "./NavBar"

const Container = styled.div`
    width: 100%;
    height: 740px;
    background-color: #F6F9FC;
    font-family: 'Lato', sans-serif;
    color: #111439;
    padding-top: 20px;
`

const ContentContainer = styled.div`
    width: 1100px;
    height: 600px;
    padding-top: 30px;
    margin-left: 50%;
    transform: translateX(-50%);
    // background-color: #f00;
`

const DotLine1 = styled.div`
    position: absolute;
    width: 1px;
    margin-top: 90px;
    background-image: linear-gradient(#EBEBEB 50%, rgba(255,255,255,0) 0%);
    background-size: 1px 12px;
    background-repeat: repeat-y;
    height: 540px;
`

const DotLine2 = styled.div`
    position: absolute;
    right: 0;
    width: 1px;
    margin-top: 90px;
    background-image: linear-gradient(#EBEBEB 50%, rgba(255,255,255,0) 0%);
    background-position: right;
    background-size: 1px 12px;
    background-repeat: repeat-y;
    height: 540px;
`

const TextContainer = styled.div`
    margin-top: 80px;
    margin-left: 50px;
`

const HelloText = styled.h1`
    font-size: 88px;
    font-weight: 900;
`

const DescText = styled.h2`
    font-size: 24px;
    font-weight: 300;
    width: 500px;
    margin-top: 16px;
    line-height: 32px;
`

const DescBold = styled.span`
    font-weight: 700;
`

const DescIt = styled.span`
    font-style: italic;
`
const QuoteText = styled.h3`
    margin-top: 24px;
    color: #11143966;
    font-size: 16px;
    font-weight: 300;
    width: 500px;
    line-height: 24px;
`

const ButtonText= styled.p`
    font-size: 16px;
    font-weight: 300;
    width: 700px;
    margin-top: 24px;
`

const Button= styled.a`
    text-decoration: none;
    color: #111439;
    font-weight: 700;
    &:hover {
        color: #0503EE;
        transition: 0.1s;
    }
`

const Emoji = styled.span`
    font-style: normal;
    margin-left: 6px;
    font-size: 14px;
`

const AndSymbol= styled.span`
    margin-right: 20px;
    margin-left: 20px;
`



export default function LandingSection () {
    return (
        <Container>
            <NavBar />
            <ContentContainer>
                <DotLine1 />
                <DotLine2 />
                <TextContainer>
                    <HelloText>Hello</HelloText>
                    <DescText>My name is <DescIt>Ben</DescIt>, and I am a freshman studying 
                        <DescIt> Computer Science </DescIt> at the 
                        <DescBold> University of Maryland. </DescBold></DescText>
                        <QuoteText>“The greatest lesson that I learned in all of this is that you have to start. Start now, start here, start small and keep it simple.”  - Jack Dorsey</QuoteText>
                        <ButtonText>
                            <Button href={"#"}>View My Projects <Emoji>🚚</Emoji></Button> 
                            <AndSymbol>&</AndSymbol>
                            <Button href={"#"}>Get In Touch <Emoji>📮</Emoji></Button>
                        </ButtonText>
                </TextContainer>

            </ContentContainer>
        </Container>
    )
}
