import React from 'react'
import styled from "styled-components"
import BlankPhone from "../images/sideways-phone-blank.svg"

const Container = styled.div`
    width: 100%;
    height: 400px;
    background-color: #fff;
    font-family: 'Lato', sans-serif;
    color: #111439;
    padding-top: 20px;
    margin-top: 60px;
    // background: #f00;
`

const ContentContainer = styled.div`
    width: 1100px;
    height: 400px;
    margin-left: 50%;
    transform: translateX(-50%);
    display: flex;
`

const TextContainer = styled.div`
    margin-left: 50px;
    // background-color: #f00;
    width: 40%;
`

const ProjectIdDiv = styled.div`
    position: absolute;
    left: -1px;
    width: 2px;
    height: 14px;
    background: -webkit-linear-gradient(15deg, ${props => props.Color1}, ${props => props.Color2});
`

const ProjectId = styled.h2`
    font-size: 14px;
    color: #CDCDE3;
    margin-bottom: 20px;
    background: -webkit-linear-gradient(${props => props.Color1}, ${props => props.Color2});
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;font-weight: 700;

`

const ProjectHeader = styled.h1`
    font-size: 32px;
    font-weight: 800;
`

const ProjectText = styled.p`
    margin-top: 20px;
    font-weight: 300;
    line-height: 24px;
    margin-bottom: 20px;
`

const PhoneContainer = styled.div`
    width: 50%;
    margin-top: -70px;
    // background-color: #0f0;
    height: 300px;
`

const PhoneImg= styled.img`
    margin-left: 30px;
    transition: 0.2s;
    // &:hover {
    //     transform: scale(100.01);
    // }
`

const ViewMoreButton = styled.a`
    font-weight: 500;
    line-height: 24px;
    color: #CDCDE3;
    text-decoration: none;
    transition: 0.2s;
`

const GitHubIconHolder = styled.div`
    font-size: 1.6rem;
    color: #DADDED;
    position: absolute;
    top: 10px;
    right: -6px;
    transition: 0.2s;
    &:hover {
        color: #A5ACD9;
        // text-shadow: 0px 2px 3px #DADDED55;
    }
`

const LinkIconHolder = styled.div`
    font-size: 1.3rem;
    color: #DADDED;
    position: absolute;
    top: 48px;
    right: -6px;
    transition: 0.2s;
    &:hover {
        color: #A5ACD9;
        // text-shadow: 0px 2px 3px #DADDED55;
    }
`

const Arrow1 = styled.div`
    width: 2px;
    height: 8px;
    background-color: #DADDED;
    transform:rotate(90deg);
    position: absolute;
    transform: rotate(45deg);
    top: 14px;
    left: 190px;
`

const Arrow2 = styled.div`
    width: 2px;
    height: 7px;
    background-color: #DADDED;
    position: absolute;
    transform: rotate(-45deg);
    top: 10px;
    left: 190px;
`

const Arrow3 = styled.div`
    width: 9px;
    height: 2px;
    position: absolute;
    top: 15px;
    left: 189px;
    background-color: #A5ACD9;
    opacity: 0;
    // border-radius: 1px;
`

const ButtonHolder = styled.div`
    transition: 0.1s;
    width: 150px;
    position: absolute;
    // background-color: #f00;
    &:hover {
        ${ViewMoreButton} {
            color: #A5ACD9;
          }
          ${Arrow1} {
            transition: 0.1s ease-in-out;
            background-color: #A5ACD9;
            left: 196px;
          }
          ${Arrow2} {
            transition: 0.1s ease-in-out;
            background-color: #A5ACD9;
            left: 196px;
          }
        ${Arrow3} {
            transition: 0.1s ease-in;
            opacity: 1;
        }
    }
`

const PlaceholderContainer = styled.div`
    position: absolute;
    z-index: 9;
    top: 92px;
    right: 250px;
    text-align: center;
`

const Placeholder1 = styled.p`
    font-size: 3.4rem;
    opacity: 0.88;
`

const Placeholder2 = styled.p`
    font-size: 0.8em;
    font-weight: 600;
    color: #494B6422;
`

export default function ProjectSection ({Color1, Color2, ProjectType, ProjectTitle, ProjectDesc, ProjectDisplay}) {
    return (
        <Container>
            <ContentContainer>
            <TextContainer>
            <ProjectIdDiv Color1={Color1} Color2={Color2}></ProjectIdDiv>
            <ProjectId Color1={Color1} Color2={Color2}>{ProjectType}</ProjectId>
            <ProjectHeader>{ProjectTitle}</ProjectHeader>
            <ProjectText>{ProjectDesc}</ProjectText>
            <GitHubIconHolder><i class="fab fa-github-alt"></i></GitHubIconHolder>
            <LinkIconHolder><i class="fas fa-external-link-alt"></i></LinkIconHolder>
            <ButtonHolder>
                <ViewMoreButton target="_blank" href="https://google.com" >View More Details </ViewMoreButton>
                <Arrow1 />
                <Arrow2 />
                <Arrow3 />
            </ButtonHolder>
            </TextContainer>
            <PhoneContainer>
                    <PhoneImg src={BlankPhone}/>
                    <PlaceholderContainer>
                        <Placeholder1>ðŸš§</Placeholder1>
                        <Placeholder2>In Progress</Placeholder2>
                    </PlaceholderContainer>
                </PhoneContainer>
            </ContentContainer>
        </Container>
    )
}

