//     }
// // `

const TylerImg = styled.img`
    position: absolute;
    top: 327px;
    right: 267px;
    width: 140px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.04);
        opacity: 0.8;
    }
// `

const KevinImg = styled.img`
    position: absolute;
    top: 60px;
    right: 97px;
    width: 310px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.04);
        opacity: 0.8;
    }
`

