// `

const PhotosImg = styled.img`
    position: absolute;
    top: 190px;
    right: 266px;
    width: 142px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.06);
        opacity: 0.8;
    }
`

const TwitchImg = styled.img`
    position: absolute;
    top: 190px;
    right: 180px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

const SpotifyImg = styled.img`
    position: absolute;
    top: 190px;
    right: 100px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

const SwiftImg = styled.img`
    position: absolute;
    top: 444px;
    right: 95px;
    width: 63px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const ReactAppImg = styled.img`
    position: absolute;
    top: 448px;
    right: 152px;
    width: 56px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const JSImg = styled.img`
    position: absolute;
    top: 444px;
    right: 202px;
    width: 63px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const PhoneImg= styled.img`
    margin-left: 50px;
`

export default function AboutSection ({ theme }) {

    const randStreamer = () => {
        const streamers = ["xqcow", "lirik", "linkus7", "pokelawls", "clintstevens", "sodapoppin", "becca", "emilyywang"]
        const n = Math.floor(Math.random() * 8)
        return streamers[n]
    }

    const sectionRef = useRef(null)

    const intersection = useIntersection(sectionRef, {
        root: null,
        rootMargin: '0px',
        threshold: 1,
    })

    const fadeIn = element => {
        gsap.to(element, 1, {
            opacity: 1,
            y: 30,
            ease: 'power4.out',
            stagger: {
                amount: 0.3
            },
        })
    }

    const fadeOut = element => {
        gsap.to(element, 1, {
            opacity: 0,
            y: 20,
            ease: 'power4.out',
        })
    }

    intersection && intersection.intersectionRatio < 1 ?
    fadeOut(".fadeIn")
    :  
    fadeIn(".fadeIn")

    const [streamer, setStreamer] = useState(randStreamer())

    return (
        <Container theme={theme}>
            <ContentContainer ref={sectionRef}>
            <TextContainer >
            <AboutIdDiv theme={theme}></AboutIdDiv>
            <AboutId className="fadeIn" theme={theme}>Who am I ?</AboutId>
            <AboutHeader className="fadeIn">A Little About Me ✌️</AboutHeader>
            <AboutText className="fadeIn" theme={theme}>
                Hi! My name is Ben and I enjoy creating things that live on the internet. 
                My interest in web development started back in 2012 when I decided to try editing 
                custom Tumblr themes — turns out hacking together a custom reblog button taught me 
                a lot about HTML & CSS!
            </AboutText>
            <AboutText className="fadeIn" theme={theme}>
                Fast-forward to today, and I've had the privilege of working at an advertising agency, 
                a start-up, a huge corporation, and a student-led design studio. My main focus these 
                days is building accessible, inclusive products and digital experiences at Upstatement 
                for a variety of clients. My main focus these 
                days is building accessible, inclusive products and digital experiences at Upstatement 
                for a variety of clients.
                </AboutText>
            </TextContainer>
            <PhoneContainer className="fadeIn">
                    <PhoneImg src={theme === "light" ? LightPhone : DarkPhone}/>
                    <PhoneContentContainer>
                        <a href="https://iribe.umd.edu/" target="_blank">
                            <ZeldaImg src={Zelda}></ZeldaImg>
                        </a>
                        <a href="https://www.youtube.com/channel/UCESLZhusAkFfsNsApnjF_Cg" target="_blank">
                            <TylerImg src={Tyler}></TylerImg>
                        </a>
                        {/* <a href="https://open.spotify.com/artist/5INjqkS1o8h1imAzPqGZBb" target="_blank"> */}
                        <a href="https://open.spotify.com/artist/7wbkl3zgDZEoZer357mVIw" target="_blank">
                            <KevinImg src={Kevin}></KevinImg>
                        </a>
                        <a href="https://www.instagram.com/dimarco.photo/" target="_blank">
                            <PhotosImg src={theme === "light" ? Photos : PhotosDark}></PhotosImg>
