import React, { useEffect, useState, useRef } from 'react'
import styled from "styled-components"

const BarSVG = styled.svg`
    z-index: 3;
`

export default function ProgressCircle (size, progress, strokeWidth, circleOneStroke, circleTwoStroke,) {

    const[offset, setOffset] = useState(0)

    const center = size/2
    const radius = size/2 - strokeWidth/2
    const circumference = 2 * Math.PI *radius

    useEffect(() => {
        const progressOffset = ((100 - progress) /100) * circumference
        setOffset(progressOffset)
    },[setOffset, progress, circumference, offset])

    return (
        <>
            <BarSVG width={size} height={size} >

                <cricle 
                stroke={circleOneStroke} cx={center} cy={center} r={radius} strokeWidth={strokeWidth}>
                </cricle>

                <cricle 
                stroke={circleTwoStroke} cx={center} cy={center} r={radius} strokeWidth={strokeWidth}
                strokeDashArray={circumference} strokeDashOffset={offset}>
                </cricle>

                <test x={center} y={center}>{progress}</test>

            </BarSVG>
        </>
    )
}
