    &:hover {
        // transform: scale(1.05);
        opacity: 0.7;
    }
`

const MailImg = styled.img`
    position: absolute;
    top: 447px;
    right: 135px;
    width: 56px;
    transition: 0.2s;
    &:hover {
        // transform: scale(1.05);
        opacity: 0.7;
    }
`

const LeafImg = styled.img`
    position: absolute;
    top: 220px;
    right: 197px;
    width: 110px;
`

const PodcastImg = styled.img`
    position: absolute;
    top: 100px;
    right: 135px;
    width: 56px;
    transition: 0.2s;
    &:hover {
        // transform: scale(1.05);
        opacity: 0.7;
    }
`

const BookImg = styled.img`
    position: absolute;
    top: 160px;
    right: 135px;
    width: 56px;
    transition: 0.2s;
    &:hover {
        // transform: scale(1.05);
        opacity: 0.7;
    }
`

const NotesImg = styled.img`
    position: absolute;
    top: 160px;
    right: 135px;
    width: 56px;
    transition: 0.2s;
    &:hover {
        // transform: scale(1.05);
        opacity: 0.7;
    }
`

const IribeImg = styled.img`
    position: absolute;
    top: 66px;
    right: 96px;
    width: 320px;
    transition: 0.2s;
`

