    position: absolute;
    top: 190px;
    right: 266px;
    width: 142px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.06);
        opacity: 0.8;
    }
`

const TwitchImg = styled.img`
    position: absolute;
    top: 190px;
    right: 180px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

const SpotifyImg = styled.img`
    position: absolute;
    top: 190px;
    right: 100px;
    width: 88px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.08);
        opacity: 0.8;
    }
`

