const LoaderDiv = styled.div`
  display: flex;
  justify-content: center; 
  align-items: center;
  width: 100%;
  height: 100vh;
  background-color: #F6F9FC;
  transition: 0.2s ease-in-out;
`

function App() {

  const [loading, setLoading] = useState(false) //true

  // useEffect(() => {
  //   setLoading(false)
  //   // setTimeout(() => {
  //   //   setLoading(false)
  //   // }, 3000)
  // }, [])

  return (
    <div>
      { loading ? 
      <LoaderDiv>
          <BarLoader 
            color={"#FF7474"} 
            loading={loading} 
            size={30} /> 
      </LoaderDiv>
      : 
        <div className="App">
          <LandingSection />
          <AboutSection />
          <ProjectSection 
            Color1="#6E29FF" 
            Color2="#C479FF" 
            ProjectType="Personal Project" 
            ProjectTitle="Twitch User Profile"  
            ProjectDesc="
            A web app for visualizing personalized Spotify data. 
            View your top artists, top tracks, recently played tracks, and 
            detailed audio information about each track. Create and save new 
            playlists of recommended tracks based on your existing playlists 
            and more." 
            ProjectDisplay={<></>} />
          <ProjectSection 
            Color1="#3F1BFF" 
            Color2="#359EFF" 
            ProjectType="Personal Project" 
            ProjectTitle="Twitch User Profile"  
            ProjectDesc="
            A web app for visualizing personalized Spotify data. 
            View your top artists, top tracks, recently played tracks, and 
            detailed audio information about each track. Create and save new 
            playlists of recommended tracks based on your existing playlists 
            and more." 
            ProjectDisplay={<></>} />
          {/* <ProjectSection /> */}
          <FooterSection />
        </div>
      } 
    </div>
  );
}

export default App;
