import React, { useEffect, useState } from 'react'
import styled from "styled-components"
import NavBar from "./NavBar"
import Scroll from "./Scroll"
import ProgressCircle from "./ProgressCircle"
// import BlankPhone from "../images/blank-phone-vertical.svg"
import BlankPhone from "../images/blank-phone-vertical.svg"
import Dock from "../images/LandingPhone/light-dock-landing.svg"
import GitHub from "../images/LandingPhone/github.svg"
import Dribbble from "../images/LandingPhone/dribbble.svg"
import FingerprintSrc from"../images/fingerprint.svg"
import { useSpring, animated, config } from "react-spring"

const Container = styled.div`
    width: 100%;
    height: 740px;
    background-color: #F6F9FC;
    font-family: 'Lato', sans-serif;
    color: #111439;
    padding-top: 20px;
`

const ContentContainer = styled.div`
    width: 1100px;
    height: 600px;
    padding-top: 30px;
    margin-left: 50%;
    transform: translateX(-50%);
    display: flex;
`

const DotLine1 = styled.div`
    position: absolute;
    width: 1px;
    margin-top: 90px;
    background-image: linear-gradient(#EBEBEB 50%, rgba(255,255,255,0) 0%);
    background-size: 1px 12px;
    background-repeat: repeat-y;
    height: 1720px;
`

const DotLine2 = styled.div`
    position: absolute;
    right: 0;
    width: 1px;
    margin-top: 90px;
    background-image: linear-gradient(#EBEBEB 50%, rgba(255,255,255,0) 0%);
    background-position: right;
    background-size: 1px 12px;
    background-repeat: repeat-y;
    height: 540px;
`

const TextContainer = styled.div`
    margin-top: 90px;
    margin-left: 50px;
    // background-color: #f00;
    width: 50%;
`

const HelloText = styled.h1`
    font-size: 88px;
    font-weight: 900;
`

const DescText = styled.h2`
    font-size: 24px;
    font-weight: 300;
    width: 500px;
    margin-top: 16px;
    line-height: 32px;
`

const DescBold = styled.span`
    font-weight: 700;
`

const DescIt = styled.span`
    font-style: italic;
`
const QuoteText = styled.h3`
    margin-top: 12px;
    color: #11143966;
    font-size: 16px;
    font-weight: 300;
    width: 500px;
    line-height: 24px;
`

const ButtonText= styled.p`
    font-size: 16px;
    font-weight: 300;
    width: 700px;
    margin-top: 24px;
`

const Button= styled.a`
    text-decoration: none;
    color: #111439;
    font-weight: 700;
    &:hover {
        color: #0503EE;
        transition: 0.1s;
    }
`

const Emoji = styled.span`
    font-style: normal;
    margin-left: 6px;
    font-size: 14px;
`

const AndSymbol= styled.span`
    margin-right: 20px;
    margin-left: 20px;
`

const PhoneContainer = styled.div`
    width: 50%;
    margin-top: -30px;
    margin-left: -30px;
    // background-color: #0f0;
`

const PhoneImg= styled.img`
    margin-left: 50px;
`

const FingerprintImg = styled(animated.img)`
    position: absolute;
    z-index: 2;
    right: 230px;
    top: 272px;
    // opacity: 0;
`

const ProgressCircleHolder = styled.div`
    position: absolute;
    z-index: 3;
    right: 176px;
    top: 272px;
`

const ScrollHolder = styled.div`
    position: absolute;
    top: 500px;
    left: 44px;
`

function Fingerprint() {
    const props = useSpring ({
        to: { opacity: 1 },
        from: { opacity: 0 },
        reset: false,
        delay: 2600,
        config: config.molasses
    })
    return <FingerprintImg src={FingerprintSrc} />
}

const PhoneContentContainer = styled.div`

`

const PhoneDockImg= styled.img`
    position: absolute;
    top: 434px;
    right: 120px;
    width: 272px;
    transition: 0.2s;
`


const GitHubImg = styled.img`
    position: absolute;
    top: 447px;
    right: 324px;
    width: 52px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

const DribbbleImg = styled.img`
    position: absolute;
    top: 447px;
    right: 260px;
    width: 52px;
    transition: 0.2s;
    &:hover {
        transform: scale(1.1);
        opacity: 0.8;
    }
`

// function Text() {
//     const props = useSpring({
//       to: { opacity: 1 },
//       from: { opacity: 0 },
//       reset: true,
//       delay: 1000,
//       config: config.molasses,
//     })
//   }

// const progressCircleState = {
//     size: 100,
//     progress: 68,
//     strokeWidth: 6,
// }

export default function LandingSection () {
    return (
        <Container>
            <NavBar />
            <ContentContainer>
                <DotLine1 />
                <DotLine2 />
                <TextContainer>
                    <HelloText>Hello</HelloText>
                    <DescText>My name is <DescIt>Ben</DescIt>, and I am a freshman studying 
                        <DescIt> Computer Science </DescIt> at the 
                        <DescBold> University of Maryland. </DescBold></DescText>
                        <QuoteText>“The greatest lesson that I learned in all of this is that you have to start. Start now, start here, start small and keep it simple.”  <i>- Jack Dorsey</i></QuoteText>
                        <ButtonText>
                            <Button href={"#"}>View My Projects <Emoji>🚚</Emoji></Button> 
                            <AndSymbol>&</AndSymbol>
                            <Button href={"#"}>Get In Touch <Emoji>📮</Emoji></Button>
                        </ButtonText>
                </TextContainer>
                <PhoneContainer>
                    <PhoneImg src={BlankPhone}/>
                    <PhoneDockImg src={Dock} />
                    <GitHubImg src={GitHub}></GitHubImg>
                    <DribbbleImg src={Dribbble}></DribbbleImg>
                    {/* <ProgressCircleHolder>
                        pain
                    </ProgressCircleHolder> */}
                    {/* <FingerprintImg src={FingerprintSrc}></FingerprintImg> */}
                </PhoneContainer>
                <ScrollHolder>
                <Scroll />
            </ScrollHolder>
            </ContentContainer>
        </Container>
        // <>
        //     <ProgressCircle size={100} progress={68} strokeWidth={6} circleOneStroke="E8EAEF" circleTwoStroke="FF7474" />
        // </>
    )
}

// {Fingerprint()}
